const items = [];
for (const item of $input.all()) {
  const webhookBody = item.json.body; // Ambil bagian 'body' dari webhookData

  // Ekstraksi data berdasarkan format webhook baru
  let phoneNumber = webhookBody.from;
  if (phoneNumber && phoneNumber.endsWith('@s.whatsapp.net')) {
    phoneNumber = phoneNumber.replace('@s.whatsapp.net', '');
  }
  const messageContent = webhookBody.message.text;
  const senderName = webhookBody.pushname; // Mengambil nama pengirim

  const leadId = `LEAD-${Date.now()}-${Math.floor(Math.random() * 1000)}`; // ID unik
  const entryDate = webhookBody.timestamp || new Date().toISOString(); // Gunakan timestamp dari webhook jika ada
  const lastInteraction = entryDate;

  items.push({
    json: {
      leadId: leadId,
      phoneNumber: phoneNumber,
      senderName: senderName, // Menambahkan senderName
      initialMessage: messageContent,
      entryDate: entryDate,
      lastInteraction: lastInteraction,
      status: "New Lead", // Status awal
      nurtureStage: 0, // Tahap nurturing awal (0 berarti belum mulai follow-up terjadwal)
      nextFollowUpDate: null // Akan diisi oleh Workflow B
    }
  });
}
return items;

const item = $input.item;
const lead = item.json;

// 1. Ambil data dengan nilai default yang aman
const status = lead.status || "";
const currentNurtureStage = parseInt(lead.nurtureStage || 0); // Pastikan angka, default 0
const nextFollowUpDateStr = lead.nextFollowUpDate; // Bisa null atau string

let shouldFollowUp = false;

// Kondisi 1: Status BUKAN "Deal", "Inactive", atau "Nurturing Complete"
const isStatusEligible = status !== "Deal" && status !== "Inactive" && status !== "Nurturing Complete";

// Kondisi 2: Nurture Stage belum mencapai batas maksimal (misal, 4 tahap: 0, 1, 2, 3)
const isStageEligible = currentNurtureStage < 4;

// Kondisi 3: nextFollowUpDate sudah tiba ATAU belum diset (null/empty)
let isDateEligible = false;
if (!nextFollowUpDateStr) {
  // Jika nextFollowUpDate null atau kosong, berarti ini lead baru atau belum dijadwalkan follow-up
  // atau sudah lewat dan belum di-update. Anggap eligible untuk follow-up.
  isDateEligible = true;
} else {
  // Jika nextFollowUpDate ada, cek apakah sudah melewati atau sama dengan hari ini
  const nextFollowUpDate = new Date(nextFollowUpDateStr);
  const now = new Date();

  // Reset jam/menit/detik untuk perbandingan HANYA tanggal (jika ingin per hari)
  // Atau biarkan full timestamp jika ingin akurat per jam/menit
  nextFollowUpDate.setHours(0, 0, 0, 0); // Hapus waktu agar hanya bandingkan tanggal
  now.setHours(0, 0, 0, 0);

  isDateEligible = nextFollowUpDate.getTime() <= now.getTime();
}

// Gabungkan semua kondisi dengan AND
shouldFollowUp = isStatusEligible && isStageEligible && isDateEligible;

// Tambahkan flag shouldFollowUp ke output
return {
  json: {
    ...lead, // Salin semua data lead yang sudah ada
    shouldFollowUp: shouldFollowUp
  }
};

const item = $input.item;
const lead = item.json;

// Pastikan nurtureStage adalah angka
const currentNurtureStage = parseInt(lead.nurtureStage || 0);

let messageText = "";
let newNurtureStage = currentNurtureStage + 1; // Tahap nurturing selanjutnya
let delayDays = 0; // Default delay untuk follow-up berikutnya (dalam hari)

// Gunakan nama jika ada, kalau tidak pakai nomor telepon
const personName = lead.senderName || lead.phoneNumber;

switch (currentNurtureStage) {
  case 0: // Tahap awal setelah welcome message
    messageText = `Halo ${personName}, ini saya dari Axiamasi. Semoga sehat selalu ya. Mau tanya, apakah ada pertanyaan lebih lanjut tentang produk/layanan kami setelah pesan kemarin?`;
    delayDays = 2; // Follow-up 2 hari lagi
    break;
  case 1:
    messageText = `Hai lagi ${personName}! Jangan lewatkan penawaran spesial kami bulan ini untuk produk A. Cek detailnya di sini: https://linkproduk.com. Promo terbatas lho!`;
    delayDays = 3; // Follow-up 3 hari lagi
    break;
  case 2:
    messageText = `Halo ${personName}, banyak customer kami sudah merasakan manfaat produk A. Ini ada beberapa testimoni yang bisa Anda lihat: https://linktestimoni.com. Jika ada pertanyaan atau butuh demo, jangan ragu chat saya ya!`;
    delayDays = 5; // Follow-up 5 hari lagi
    break;
  case 3: // Final follow-up (atau penawaran terakhir)
    messageText = `Halo ${personName}, ini adalah pesan follow-up terakhir dari kami untuk Anda. Apakah Anda tertarik untuk segera memulai atau mendapatkan konsultasi gratis? Jika tidak ada respons dalam 24 jam, kami akan menganggap Anda belum berminat untuk saat ini.`;
    newNurtureStage = 99; // Tandai sebagai nurturing selesai (atau 'lost' jika tidak ada respons)
    delayDays = 0; // Tidak ada follow-up selanjutnya dari alur ini
    break;
  default:
    // Jika stage tidak dikenal atau sudah melewati tahap akhir
    messageText = ""; // Jangan kirim pesan
    newNurtureStage = currentNurtureStage; // Tidak mengubah stage
    delayDays = 0;
    break;
}

// Hanya proses jika ada pesan yang akan dikirim
if (messageText) {
  // Hitung nextFollowUpDate
 // Hitung nextFollowUpDate
let nextFollowUpDate = null;
if (delayDays > 0) {
  const date = new Date();
  date.setDate(date.getDate() + delayDays);
  date.setHours(9, 0, 0, 0); // Kita set jam 9 pagi untuk next follow-up
  nextFollowUpDate = date.toISOString(); // Pastikan format ISO String
} else {
  // Jika nurturing selesai, set status atau biarkan null (akan di-handle di node sheets)
  nextFollowUpDate = null; // Tidak ada follow-up berikutnya
}

return {
  json: {
    ...lead, // Salin semua data lead
    messageToSend: messageText,
    newNurtureStage: newNurtureStage,
    nextFollowUpDate: nextFollowUpDate // Pastikan ini dikirim
  }
};
} else {
  // Jika tidak ada pesan yang akan dikirim, return item kosong atau filter nanti
  // Untuk saat ini, kita return data lead aslinya tanpa perubahan jika messageText kosong
  return item;
}
